function word_balloon_change_avatar_effect(){word_balloon_reset_effect("avatar","w_b_ava_effect");}function word_balloon_change_balloon_effect(){word_balloon_reset_effect("balloon","w_b_bal_outer");}function word_balloon_change_icon_effect(){word_balloon_reset_effect("icon","w_b_icon_effect");}function word_balloon_reset_effect(type,target){var effect=eval("word_balloon_get_"+type+"_effect()");target=document.getElementById(target);var arr=JSON.parse(document.getElementById("w_b_type_effect").dataset.type);for(var i=0;i<arr.length;i++){target.classList.remove("w_b_"+arr[i]);}if(effect!==""){target.classList.add("w_b_"+effect);}}function word_balloon_effect_duration_change(a,c){if(!document.getElementById("w_b_edit_"+a+"_effect_duration")){return;}var b="";if(a==="avatar"){b=document.getElementById("w_b_ava_effect");}else{if(a==="balloon"){b=document.getElementById("w_b_bal_outer");}else{if(a==="icon"){b=document.getElementById("w_b_icon_effect");}}}b.style.animationDuration=c+"s";}function word_balloon_effect_duration_clear(a){if(!document.getElementById("w_b_edit_"+a+"_effect_duration")){return;}if(a==="avatar"){document.getElementById("w_b_edit_avatar_effect_duration").value="";e=document.getElementById("w_b_ava_effect");}else{if(a==="balloon"){document.getElementById("w_b_edit_balloon_effect_duration").value="";e=document.getElementById("w_b_bal_outer");}else{if(a==="icon"){document.getElementById("w_b_edit_icon_effect_duration").value="";e=document.getElementById("w_b_icon_effect");}}}e.style.animationDuration="";}